package com.youda.dao;import com.sun.org.apache.bcel.internal.generic.ALOAD;import com.youda.model.AliPayConf;import org.apache.ibatis.annotations.*;import java.util.List;/** * @Author Chencongye * @Date 2017/12/11 13:10 * @Version 1.0.0 * @Instructions 定义支付宝支付需要配置的Dao层接口 */@Mapperpublic interface AliPayConfMapper {    /*添加支付宝支付配置信息*/    @Insert("insert into tb_alipayconf(ALIPAY_PUBLIC_KEY,APP_ID,APP_PRIVATE_KEY,CALLBACK_URL,NOTIFY_URL,GAME_NAME,GAME_PACKAGE) values(#{aliPayConf.ALIPAY_PUBLIC_KEY},#{aliPayConf.APP_ID},#{aliPayConf.APP_PRIVATE_KEY},#{aliPayConf.CALLBACK_URL},#{aliPayConf.NOTIFY_URL},#{aliPayConf.GAME_NAME},#{aliPayConf.GAME_PACKAGE})")    boolean addAliPayConf(@Param("aliPayConf") AliPayConf aliPayConf);    /*根据支付宝配置的Id来删除支付宝配置信息*/    @Delete("delete from tb_alipayconf where alipayId=#{alipayId}")    boolean deleteAliPayConf(@Param("alipayId") long alipayId);    /*根据支付宝配置修改支付宝配置信息*/    @Update("update tb_alipayconf set ALIPAY_PUBLIC_KEY=#{ALIPAY_PUBLIC_KEY},APP_ID=#{APP_ID},APP_PRIVATE_KEY=#{APP_PRIVATE_KEY},CALLBACK_URL=#{CALLBACK_URL},NOTIFY_URL=#{NOTIFY_URL},GAME_NAME=#{GAME_NAME},GAME_PACKAGE=#{GAME_PACKAGE}")    boolean modifyAliPayConf(@Param("aliPayConf") AliPayConf aliPayConf);    /*根据支付宝配置Id来查询支付宝配置信息*/    @Select("select * from tb_alipayconf where alipayId=#{alipayId}")    AliPayConf findByAliPayConfId(@Param("alipayId") long alipayId);    /*查找所有的支付宝配置信息*/    @Select("select * from tb_alipayconf")    List<AliPayConf> findAllAliPayConf();    /*通过APP_ID来查找支付宝支付的配置信息*/    @Select("select * from tb_alipayconf where APP_ID=#{APP_ID}")    AliPayConf findByAliPayAppId(@Param("APP_ID") String APP_ID);}